import { CommonModule } from '@angular/common';
import { ChangeDetectionStrategy, Component, EventEmitter, Input, Output } from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { EmailInputComponent, RequiredInputComponent, RequiredTextboxComponent } from '@hra-ui/components/atoms';
import { produce } from 'immer';
import { MarkdownModule } from 'ngx-markdown';

/**
  An interface representing contact data information which will be emitted if all the fields are in correct format.
 */
export interface ContactData {
  /**  The email address of the person submitting the form. */
  email: string;
  /** The subject line of the message being sent. */
  subject: string;
  /** The body of the message being sent. */
  message: string;
}

/** Initial empty contact data */
const EMPTY_CONTACT_DATA: Partial<ContactData> = {
  email: undefined,
  subject: undefined,
  message: undefined,
};

/**
 * Contact modal component.
 */
@Component({
  selector: 'hra-contact-modal',
  standalone: true,
  imports: [
    CommonModule,
    MatButtonModule,
    MatIconModule,
    EmailInputComponent,
    MarkdownModule,
    RequiredTextboxComponent,
    RequiredInputComponent,
  ],
  templateUrl: './contact-modal.component.html',
  styleUrls: ['./contact-modal.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class ContactModalComponent {
  /** Input for product logo URL to displayed on the left side. */
  @Input() productLogoUrl = '';

  /** Input for product title to displayed on the left side. */
  @Input() productTitle = '';

  /** Emits when the submit button is clicked and all the values are appropriate. */
  @Output() readonly submitClick = new EventEmitter<ContactData>();

  /** Emits when the cancel button or close icon is clicked */
  @Output() readonly closeClick = new EventEmitter<void>();

  /** A function to disable the submit button. If all the values are in approproiate format, then it will be enabled. */
  get disableSubmit(): boolean {
    return Object.values(this.contactData).some((value) => value === undefined);
  }

  /** Asigns the undefined initially to all the input fields. */
  contactData = EMPTY_CONTACT_DATA;

  /** Updates the contact data object with a new value for a given key. */
  updateData<K extends keyof ContactData>(key: K, value?: ContactData[K]): void {
    this.contactData = produce(this.contactData, (draft) => {
      draft[key] = value;
    });
  }

  /** It emits the updated value of contact data */
  submit(): void {
    this.submitClick.emit(this.contactData as ContactData);
  }
}

import { ChangeDetectionStrategy, Component, Input, OnChanges, SimpleChanges } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatTableModule } from '@angular/material/table';

/** An item which defines a string label and a string value */
export interface DataItem {
  /** A string property that represents the label */
  label: string;
  /** A string property that represents the value */
  value: string;
}

/** An item which represents a section with a data item and an indicator for the last item in the section */
export interface SectionItem {
  /** A number property that represents the section number */
  section: number;
  /** A DateItem property that represents a section and its data */
  data: DataItem;
  /** A boolean property that represents the last item of a section */
  isLastItem: boolean;
}

/** This component displays a table with sections, where each section contains a list of items with a label and a value. */
@Component({
  selector: 'hra-biomarker-table-data-card',
  standalone: true,
  imports: [CommonModule, MatTableModule],
  templateUrl: './biomarker-table-data-card.component.html',
  styleUrls: ['./biomarker-table-data-card.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class BiomarkerTableDataCardComponent implements OnChanges {
  /** Nested list of DataItems for each section which is displayed to the user */
  @Input() data: DataItem[][] = [];

  /** Converted nested array of DataItem into Flat array of SectionItem */
  sectionItems: SectionItem[] = [];

  /** Triggers an update on sectionItems array when data is changed */
  ngOnChanges(changes: SimpleChanges): void {
    if ('data' in changes) {
      this.updateSectionItems();
    }
  }

  /** Updates sectionItems array when data is changed */
  private updateSectionItems(): void {
    const newSectionItems: SectionItem[] = [];

    this.data.forEach((section, sectionIndex) => {
      section.forEach((item, itemIndex) => {
        newSectionItems.push({
          data: item,
          section: sectionIndex,
          isLastItem: itemIndex === section.length - 1,
        });
      });
    });

    this.sectionItems = newSectionItems;
  }
}

import { FlatTreeControl } from '@angular/cdk/tree';
import { CommonModule } from '@angular/common';
import {
  ChangeDetectionStrategy,
  Component,
  EventEmitter,
  Input,
  OnChanges,
  Output,
  SimpleChanges,
} from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import { MatRippleModule } from '@angular/material/core';
import { MatExpansionModule } from '@angular/material/expansion';
import { MatIconModule } from '@angular/material/icon';
import { MatTreeFlatDataSource, MatTreeFlattener, MatTreeModule } from '@angular/material/tree';
import { LinkDirective } from '@hra-ui/cdk';
import { LinkId } from '@hra-ui/cdk/state';

/** Base node type */
export interface DataNode<K extends string> {
  /** User readable label */
  label: string;
  /** Id to pass as a query parameter on navigation */
  id?: string;
  /** Link to navigate to on node click */
  link?: LinkId;
  /** Nested nodes */
  children?: K[];
}

/**
 * Internal interface for flat tissue data hierarchy
 */
interface InternalNode<K extends string, T extends DataNode<K>> {
  /** Displayed label */
  label: string;
  /** Whether the node can be expanded to display child nodes */
  expandable: boolean;
  /** Depth of node in the tree */
  level: number;
  /** Associated user node data */
  data: T;
}

/**
 * Tabular View for hubMap tissue side-bar
 */
@Component({
  selector: 'hra-tissue-tree-list',
  standalone: true,
  imports: [
    CommonModule,
    MatButtonModule,
    MatTreeModule,
    MatIconModule,
    MatExpansionModule,
    MatRippleModule,
    LinkDirective,
  ],
  templateUrl: './tissue-tree-list.component.html',
  styleUrls: ['./tissue-tree-list.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class TissueTreeListComponent<K extends string, T extends DataNode<K>> implements OnChanges {
  /**
   * Input  of tissue tree list component
   */
  @Input() nodes: Record<K, T> = {} as Record<K, T>;

  /**
   * Node selected, to view the data associated with it
   */
  @Input() selected?: T = undefined;

  /**
   * Output  of tissue tree list component
   */
  @Output() readonly selectedChange = new EventEmitter<T | undefined>();

  /**
   * tree controller, used to control the nodes in the tree
   */
  readonly control = new FlatTreeControl<InternalNode<K, T>>(
    (node) => node.level,
    (node) => node.expandable
  );

  /**
   * Flattener of tissue tree list component, returns flat-data structure
   */
  readonly flattener = new MatTreeFlattener<T, InternalNode<K, T>>(
    (node, level) => ({
      label: node.label,
      expandable: (node.children?.length ?? 0) > 0,
      level,
      data: node,
    }),
    (node) => node.level,
    (node) => node.expandable,
    (node) => node.children?.map((id) => this.nodes[id])
  );

  /**
   * Data source of tissue tree list component, defines the data in mat-tree
   */
  readonly dataSource = new MatTreeFlatDataSource(this.control, this.flattener);

  /**
   * Take actions if any data changes
   * @param changes changes in data
   */
  ngOnChanges(changes: SimpleChanges): void {
    if ('nodes' in changes) {
      this.dataSource.data = this.findRootNodes();
    }
    if ('selected' in changes) {
      const path = this.selected ? this.dfsFindPath(this.findRootNodes(), this.selected) : [];
      this.expandPath(path);
    }
  }

  /**
   * check if the current node has children
   * @param node current selected node
   * @returns boolean, which means if node has children
   */
  hasChild(_: number, node: InternalNode<K, T>): boolean {
    return node.expandable;
  }

  /**
   * It selects the node, which is clicked.
   * @param node Tissue Tree Item, which is clicked
   */
  selectNode(node: T): void {
    if (this.selected !== node) {
      this.selected = node;
      this.selectedChange.emit(this.selected);
    }
  }

  /**
   * Resets selection and collapes all nodes of the tree.
   */
  resetSelection(): void {
    this.selected = undefined;
    this.control.collapseAll();
  }

  /**
   * It creates a copy of the input nodes object.
   * It iterates over it and removes all the children nodes from it.
   * @returns remaining nodes which are root nodes.
   */
  private findRootNodes(): T[] {
    const { nodes } = this;
    const roots = { ...this.nodes };
    for (const key in nodes) {
      for (const child of nodes[key].children ?? []) {
        delete roots[child];
      }
    }

    return Object.values(roots);
  }

  /**
   * expands the tree nodes based on the path provided.
   * @param path is given as an input.
   */
  private expandPath(path: DataNode<K>[]): void {
    const nodes = this.control.dataNodes.filter((node) => path.includes(node.data));
    nodes.forEach((node) => this.control.expand(node));
  }

  /**
   * It used the logic of depth first search to find the target node.
   * returns the path to the target node.
   */
  private dfsFindPath(nodes: T[], target: T, path: T[] = []): T[] {
    for (const node of nodes) {
      path.push(node);
      if (node === target) {
        return path;
      }

      const savedLength = path.length;
      const children = node.children?.map((id) => this.nodes[id]) ?? [];
      if (this.dfsFindPath(children, target, path).length > savedLength) {
        return path;
      }

      path.pop();
    }

    return path;
  }
}

import { ChangeDetectionStrategy, Component, EventEmitter, HostBinding, Input, Output } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatIconModule } from '@angular/material/icon';
import { MatButtonModule } from '@angular/material/button';
import { ConnectedPosition, OverlayModule } from '@angular/cdk/overlay';
import { MatListModule } from '@angular/material/list';
import { LinkDirective } from '@hra-ui/cdk';
import { EMPTY_LINK } from '@hra-ui/cdk/state';

/**
 * Base type for different download format options.
 */
export interface DownloadFormat {
  /**
   * User readable format label
   */
  label: string;
}

/**
 * Positioning value for the download list popup
 */
const DOWNLOADS_LIST_POSITION: ConnectedPosition[] = [
  {
    panelClass: 'above',
    originX: 'start',
    originY: 'top',
    overlayX: 'start',
    overlayY: 'bottom',
    offsetY: 0,
  },
  {
    panelClass: 'below',
    originX: 'start',
    originY: 'bottom',
    overlayX: 'start',
    overlayY: 'top',
  },
];

/**
 * Component for footer that displays its content at the bottom of the page with different action buttons.
 */
@Component({
  selector: 'hra-footer',
  standalone: true,
  imports: [CommonModule, MatButtonModule, MatIconModule, OverlayModule, MatListModule, LinkDirective],
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class FooterComponent<T extends DownloadFormat = DownloadFormat> {
  /** Host binding of footer component */
  @HostBinding('class')
  @Input()
  size: 'small' | 'large' = 'large';

  /** Input for product logo URL to displayed on the left side. */
  @Input() productLogoUrl = '';

  /** Input for product title to displayed on the left side. */
  @Input() productTitle = '';

  /** Input for HRA Portal link */
  @Input() hraPortal = EMPTY_LINK;

  /** Input for Illustration metadata page link in HRA Portal */
  @Input() illustrationMetadata = EMPTY_LINK;

  /** Input for embed link in HRA Portal */
  @Input() embed = EMPTY_LINK;

  /** Different download formats options displayed to the user */
  @Input() downloadFormats: T[] = [];

  /** Emits the selected download format */
  @Output() readonly download = new EventEmitter<T>();

  /** Emits when the contact button is clicked */
  @Output() readonly contactClick = new EventEmitter<void>();

  /** Download list popup overlay positioning */
  readonly DOWNLOADS_LIST_POSITION = DOWNLOADS_LIST_POSITION;

  /** Gets whether the footer required is small */
  get isSmall(): boolean {
    return this.size === 'small';
  }

  /** Whether the download list panel is open */
  downloadListOpen = false;
}

